    chuckNorris: function() {
        var obterInformacoesChuckNorris = new sn_ws.RESTMessageV2();
        obterInformacoesChuckNorris.setEndpoint("https://api.chucknorris.io/jokes/random");
        obterInformacoesChuckNorris.setHttpMethod("GET");
        var Response = obterInformacoesChuckNorris.execute();
        var Info = Response.getBody();
        Info = JSON.parse(Info);
        gs.info("INFORMAÇÕES CHUCK NORRIS: 	" + Info.value);
        gs.info("INFORMAÇÕES CHUCK NORRIS: 	" + Info.updated_at);

    },

    consultarCEP: function(CEP) {


        var obterCEP = new sn_ws.RESTMessageV2();
        obterCEP.setEndpoint("https://viacep.com.br/ws/${cep}/json/");
        obterCEP.setStringParameterNoEscape("cep", CEP);
        obterCEP.setHttpMethod("GET");

        var Response = obterCEP.execute();
        var ResponseBody = Response.getBody();
        var HttpStatus = Response.getStatusCode();
        ResponseBody = JSON.parse(ResponseBody);


        gs.info("STATUS CODE " + HttpStatus);
        gs.info("INFO CEP " + ResponseBody);
        gs.info("Bairro - " + ResponseBody.bairro);


    },



    type: 'Aula_9'
};
