sys_script_include.script:
var StarWars = Class.create();
StarWars.prototype = {
    initialize: function() {},

    mundoStarWars: function(Person) {
        var obterDadosPersonagem = new sn_ws.RESTMessageV2();
        obterDadosPersonagem.setEndpoint("https://swapi.dev/api/people/${person}/");
        obterDadosPersonagem.setStringParameterNoEscape("person", Person);
        obterDadosPersonagem.setHttpMethod("GET");

        var Response = obterDadosPersonagem.execute();
        var CodeStatus = Response.getStatusCode();
        var Info = Response.getBody();
        Info = JSON.parse(Info);

        var planeta = new sn_ws.RESTMessageV2();
        planeta.setEndpoint(Info.homeworld);
        planeta.setHttpMethod("GET");

        var ResponseWord = planeta.execute();
        var InfoWord = ResponseWord.getBody();
        InfoWord = JSON.parse(InfoWord);

        gs.info("STATUS CODE " + CodeStatus);
        gs.info("Nome: 	" + Info.name);
        gs.info("Altura: 	" + Info.height);
        gs.info("Peso: 	" + Info.mass);
        gs.info("Cor dos Olhos: 	" + Info.eye_color);
        gs.info("Gênero: 	" + Info.gender);
        gs.info("Planeta Natal:	  " + InfoWord.name);

    },


    type: 'StarWars'
};
